annotations.alm-examples:
[
  {
    "apiVersion": "liberty.websphere.ibm.com/v1",
    "kind": "WebSphereLibertyApplication",
    "metadata": {
      "name": "websphereliberty-app-sample"
    },
    "spec": {
      "applicationImage": "icr.io/appcafe/open-liberty/samples/getting-started@sha256:22d8d29128bb497933990f3eaa5735c9ac702e4e93b77e91d7d1f24c46cc060b",
      "license": {
        "accept": false,
        "edition": "IBM WebSphere Application Server",
        "metric": "Virtual Processor Core (VPC)",
        "productEntitlementSource": "Standalone"
      },
      "manageTLS": true
    }
  },
  {
    "apiVersion": "liberty.websphere.ibm.com/v1",
    "kind": "WebSphereLibertyDump",
    "metadata": {
      "name": "websphereliberty-dump-sample"
    },
    "spec": {
      "include": [
        "thread",
        "heap"
      ],
      "license": {
        "accept": false
      },
      "podName": "Specify_Pod_Name_Here"
    }
  },
  {
    "apiVersion": "liberty.websphere.ibm.com/v1",
    "kind": "WebSphereLibertyTrace",
    "metadata": {
      "name": "websphereliberty-trace-sample"
    },
    "spec": {
      "license": {
        "accept": false
      },
      "maxFileSize": 20,
      "maxFiles": 5,
      "podName": "Specify_Pod_Name_Here",
      "traceSpecification": "*=info:com.ibm.ws.webcontainer*=all"
    }
  }
]
